import { reactive } from "vue";

export function useModalSuccess(modalSuccess) {
    const item = reactive({});

    for ( const [key, value] of Object.entries(modalSuccess) ) {
        item[key] = value;
    }

    const modalShow = (content) => {
        for ( const [key, value] of content ) {
            item[key] = value;
        }
    }

    const modalJustClose = () => {
        item.value = {
            show: false,
            message: "",
            func: null,
            btn: null,
        } 
    }

    return {
        item,

        modalShow,
        modalJustClose
    };
}
