import { reactive, ref } from "vue";

export function useArrows(arrows = []) {
    const list = reactive([]);
    const area = ref(null);

    arrows.forEach((el) => {
        list.push(el);
    });

    const addNewArrow = (items) => {
        console.log(items);
    };

    const removeArrow = (id) => {
        console.log(id);
    };

    const clearArea = () => {};

    // const drawArrow = (arrow) => {}

    const init = (areaID) => {
        area.value = document.getElementById(areaID);
    };

    return {
        list,
        addNewArrow,
        removeArrow,

        init,
    };
}
