import { ref } from "vue";

export function useDragObject(dragObject = null) {
    const val = ref(dragObject);
    // let offsetItemX = ref(0);
    // let offsetItemY = ref(0);

    const setDragObject = (event) => {
        if(event.which !== 1) return;
        val.value = event.target.closest(".todo__item");
        // offsetItemX.value = mouseX.value - dragObject.getBoundingClientRect().left;
        // offsetItemY.value = mouseY.value - dragObject.getBoundingClientRect().top;
        val.value.classList.add("drag");
        document.body.onselectstart = function () {
            return false;
        };
    }

    const removeDragObject = () => {
        if (val.value) {
            val.value.classList.remove("drag");
            val.value = null;
            document.body.onselectstart = function () {};

            // offsetItemX.value = 0;
            // offsetItemY.value = 0;
        }
    }

    return {
        val,
        setDragObject, 
        removeDragObject,

        // offsetItemX,
        // offsetItemY
    };
}
