import { ref } from "vue";

export function useTodoItemList(todoList) {
    const list = ref([]);

    todoList.forEach(el => {
        list.value.push(el);
    });

    console.log(list.value)

    const setItemPos = (itemId, xPos, yPos) => {
        list.value.forEach((el) => {
            if(el.id == itemId) {
                el.position.x = xPos,
                el.position.y = yPos
            }
        })
    }

    return {list, setItemPos}
}
