import { reactive } from "vue";
import { useCheckList } from "./checkList";

export function useTodoItem(todoItem = {}) {
    const val = reactive({});

    for (const [key, value] of Object.entries(todoItem)) {
        // if(key == 'text') {
        //     console.log(value)
            // value.forEach(el => {
            //     if(typeof el == "string") val[key].push(el);
            //     else val[key].push(useCheckList(el))
            // });
        // }
        val[key] = value;
    }

    const setItemPos = (xPos, yPos) => {
        if(xPos) val.position.x = xPos
        if(yPos) val.position.y = yPos
    }

    const setItemMenuPos = (event) => {
        let menuType = "menu";
        const item = event.target.closest(".todo__item");
        val.menuToggle = !val.menuToggle;

        if (window.innerWidth - item.getBoundingClientRect().right < 180) {
            menuType += "-left";
        } else {
            menuType += "-right";
        }
        if (item.getBoundingClientRect().top < 120) {
            menuType += "-bottom";
        } else {
            menuType += "-top";
        }

        item.setAttribute("menu-type", menuType);

        const listener = (event) => {
            if (!event.target.closest(".todo__item")) {
                if (event.target.closest(".component")) {
                    val.menuToggle = false;
                    document.removeEventListener("click", listener, false);
                }
            } else {
                if (event.target.closest(".todo__item").getAttribute("todo-id") !== item.getAttribute("todo-id")) {
                    val.menuToggle = false;
                    document.removeEventListener("click", listener, false);
                }
            }
        };

        document.addEventListener("click", listener, false);
    }

    const setLockItem = () => {
        val.lock = !val.lock;
        val.menuToggle = false;
    }

    const addNewCheckList = () => {
        val.text.push([
            {
                id: 1,
                text: "",
                status: false,
            },
        ]);
        val.text.push("");
    }

    // const removeCheckList = (status) => {
    //     const item = getItemInDataById(todoList.value, status.itemId);
    //     const list = item.text[status.listCount];
    //     list.splice(
    //         list.findIndex((el) => el.id == status.todoId),
    //         1
    //     );

    //     if (!list.length) {
    //         if (typeof item.text[status.listCount - 1] === "string") {
    //             if (typeof item.text[status.listCount + 1] === "string") {
    //                 item.text[status.listCount - 1] += item.text[status.listCount + 1];
    //                 item.text.splice(status.listCount + 1, 1);
    //             }
    //         } else {
    //             if (typeof item.text[status.listCount + 1] !== "string") {
    //                 item.text.splice(status.listCount - 1, 0, "");
    //             }
    //         }

    //         item.text.splice(status.listCount, 1);
    //     }
    // }

    const showDeleteDiolog = () => {
        return {
            show: true,
            message: "Удалить этот пин?",
            func: {
                type: "item:delete",
                id: val.id,
            },
            btn: {
                yes: "Удалить",
                no: "Отмена",
            },
        };
    }

    return {
        val,

        setItemPos,
        setItemMenuPos,
        setLockItem,
        addNewCheckList,
        showDeleteDiolog
    }
}
