import { reactive, defineEmits } from "vue";

export function useModalSuccess(modalSuccess) {
    const emit = defineEmits(["item:remove"])
    const item = reactive({});

    for ( const [key, value] of Object.entries(modalSuccess) ) {
        item[key] = value;
    }

    const modalShow = (content) => {
        for ( const [key, value] of Object.entries(content) ) {
            item[key] = value;
        }
    }

    const modalReturnConfirm = () => {
        switch (item.func.type) {
            case 'item:remove' :
                emit("item:remove", item.func.id);
                modalJustClose();
                break;
        }
    }

    const modalJustClose = () => {
        item.show = false;
        item.message = "";
        item.func = null;
        item.btn = null;
    }

    return {
        item,

        modalShow,
        modalJustClose,
        modalReturnConfirm
    };
}
