export function useDragObject(dragObject = null) {
    const val = dragObject;
    // const event = fixEvent();

    const setDragObject = (event) => {
        if(event.which !== 1) return;
        val = event.target.closest(".todo__item");
        // offsetItemX.value = mouseX.value - dragObject.getBoundingClientRect().left;
        // offsetItemY.value = mouseY.value - dragObject.getBoundingClientRect().top;
        val.classList.add("drag");
        document.body.onselectstart = function () {
            return false;
        };

        console.log(val)
    }

    const removeDragObject = () => {
        if (val) {
            val.classList.remove("drag");
            val = null;
            document.body.onselectstart = function () {};
        }
    }

    return {val, setDragObject, removeDragObject};
}
