import { reactive, ref } from "vue";

export function useArrows(arrows = []) {
    const list = reactive([]);
    const area = ref(null);

    arrows.forEach((el) => {
        list.push(el);
    });

    const addNewArrow = (items) => {
        list.push({
            id: Math.random() * 1000,
            firstItem: items.firstItem,
            secondItem: items.secondItem,
            points: [],
        });
    };

    const removeArrow = (id) => {
        console.log(id);
    };

    const clearArea = () => {
        area.value.innerHTML = "";
    };

    // const drawArrow = (arrow) => {}

    const init = (areaID) => {
        area.value = document.getElementById(areaID);
        clearArea();

        console.log(area.value);
    };

    return {
        list,
        addNewArrow,
        removeArrow,

        init,
    };
}
