import { reactive, ref } from "vue";
import { getItemInDocById } from "../getItem";

export function useArrows(arrows = []) {
    const list = reactive([]);
    const area = ref(null);
    const arrowMode = ref(false);

    arrows.forEach((el) => {
        list.push(el);
    });

    const addNewArrow = (items) => {
        console.log(items);
        // list.push({
        //     id: Math.random() * 1000,
        //     firstItem: items.firstItem,
        //     secondItem: items.secondItem,
        //     points: [],
        // });

        // console.log(list);
    };

    const setArrow = (firstItem) => {
        const firstItemOffset = getItemInDocById(firstItem);
        if (!firstItemOffset) return;

        arrowMode.value = true;

        document.addEventListener("mousemove", mouseMovelisterenerSetArrow);

        function mouseMovelisterenerSetArrow(event) {
            const pageX = event.pageX;
            const pageY = event.pageY;
            const itemOffset = firstItemOffset.getBoundingClientRect();

            let xPos = 0;
            let yPos = 0;

            if (itemOffset.top - pageY > 100) {
                yPos = 0;
            } else if (pageY - itemOffset.bottom > 100) {
                yPos += itemOffset.height / 2;
            } else {
                if (pageX > itemOffset.right) {
                    xPos += itemOffset.width / 2;
                } else if (pageX < itemOffset) {
                    xPos = 0;
                }
            }

            drawArrow(
                itemOffset.left + xPos,
                itemOffset.top + yPos,
                pageX,
                pageY
            );
        }
    };

    // const getOffsetOfItems = (firstItem, secondItem) => {
    //     const firstItemOffset = firstItem.getBoundingClienRect();
    //     const secondItemOffset = secondItem.getBoundingClienRect();

    //     if(firstItemOffset.bottom - secondItemOffset.top > 100) { // First item on top

    //     }else if(firstItem.top - secondItem.bottom  ) { // Second item on top

    //     }
    // };

    const removeArrow = (id) => {
        console.log(id);
    };

    const clearArea = () => {
        area.value.innerHTML = "";
    };

    // const drawArrow = (x1, y1, x2, y2) => {}

    const init = (areaID) => {
        area.value = document.getElementById(areaID);
        clearArea();

        console.log(area.value);
    };

    return {
        list,
        arrowMode,

        addNewArrow,
        removeArrow,
        setArrow,

        init,
    };
}
